/* Logo Carousel Component Styles */
/* Scoped to .logo-carousel to avoid global collisions */

.logo-carousel {
  --logo-gap: 2rem;
  --animation-duration: 30s;
  position: relative;
  width: 100%;
  overflow: hidden;
  /* Inherit colors from host page */
  background-color: transparent;
  color: inherit;
}

/* Controls */
.logo-carousel-controls {
  position: absolute;
  top: 0.5rem;
  right: 0.5rem;
  z-index: 10;
}

.logo-carousel-control-button {
  display: flex;
  align-items: center;
  justify-content: center;
  width: 2.5rem;
  height: 2.5rem;
  padding: 0.5rem;
  /* Inherit from host page or use neutral fallbacks */
  background-color: var(--carousel-control-bg, rgba(0, 0, 0, 0.05));
  color: var(--carousel-control-color, currentColor);
  border: 1px solid var(--carousel-control-border, rgba(0, 0, 0, 0.1));
  border-radius: 0.375rem;
  cursor: pointer;
  transition: all 0.2s ease;
}

.logo-carousel-control-button:hover {
  background-color: var(--carousel-control-bg-hover, rgba(0, 0, 0, 0.1));
  transform: scale(1.05);
}

.logo-carousel-control-button:focus-visible {
  outline: 2px solid var(--carousel-focus-color, currentColor);
  outline-offset: 2px;
}

.logo-carousel-control-button[aria-pressed="true"] {
  background-color: var(--carousel-control-bg-active, rgba(0, 0, 0, 0.15));
}

/* Viewport */
.logo-carousel-viewport {
  position: relative;
  display: flex;
  overflow: hidden;
  width: 100%;
  padding: 2rem 0;
}

/* Track */
.logo-carousel-track {
  display: flex;
  gap: var(--logo-gap);
  align-items: center;
  flex-shrink: 0;
  will-change: transform;
  /* GPU-accelerated transform */
  transform: translateX(0);
}

/* Infinite scroll animation */
@keyframes scroll-rtl {
  from {
    transform: translateX(0);
  }
  to {
    transform: translateX(-50%);
  }
}

.logo-carousel:not(.logo-carousel--paused):not(.logo-carousel--reduced-motion-static):not(.logo-carousel--reduced-motion-paged) .logo-carousel-track {
  animation: scroll-rtl var(--animation-duration) linear infinite;
}

/* Pause on hover (if enabled) */
.logo-carousel-viewport--hoverable:hover .logo-carousel-track {
  animation-play-state: paused;
}

/* Duplicate track (for seamless loop) */
.logo-carousel-track--duplicate {
  position: absolute;
  top: 0;
  left: 100%;
}

/* Logo items */
.logo-carousel-item {
  flex-shrink: 0;
  display: flex;
  align-items: center;
  justify-content: center;
  height: 4rem;
  min-width: 6rem;
  max-width: 12rem;
}

.logo-carousel-logo-wrapper {
  display: block;
  position: relative;
  width: 100%;
  height: 100%;
  padding: 0.5rem;
  transition: transform 0.2s ease, box-shadow 0.2s ease;
  border-radius: 0.25rem;
  /* Inherit background or use transparent */
  background-color: var(--carousel-logo-bg, transparent);
}

.logo-carousel-logo-wrapper:hover,
.logo-carousel-logo-wrapper:focus-visible {
  transform: scale(1.1);
  box-shadow: 0 4px 12px var(--carousel-logo-shadow, rgba(0, 0, 0, 0.1));
}

.logo-carousel-logo-wrapper:focus-visible {
  outline: 2px solid var(--carousel-focus-color, currentColor);
  outline-offset: 4px;
}

.logo-carousel-logo {
  width: 100%;
  height: 100%;
  object-fit: contain;
  object-position: center;
  /* Preserve brand colors - don't apply filters */
  filter: none;
  user-select: none;
  -webkit-user-drag: none;
}

/* Reduced motion fallback - static (default) */
.logo-carousel--reduced-motion-static .logo-carousel-track {
  animation: none !important;
}

.logo-carousel--reduced-motion-static .logo-carousel-viewport {
  overflow-x: hidden;
}

/* Reduced motion fallback - paged (scrollable) */
.logo-carousel--reduced-motion-paged .logo-carousel-track {
  animation: none !important;
}

.logo-carousel--reduced-motion-paged .logo-carousel-viewport {
  overflow-x: auto;
  overflow-y: hidden;
  scroll-snap-type: x mandatory;
  -webkit-overflow-scrolling: touch;
}

.logo-carousel--reduced-motion-paged .logo-carousel-item {
  scroll-snap-align: center;
}

/* Paused state */
.logo-carousel--paused .logo-carousel-track {
  animation-play-state: paused;
}

/* Empty and loading states */
.logo-carousel-empty,
.logo-carousel-loading {
  padding: 2rem;
  text-align: center;
  color: var(--carousel-text-muted, rgba(0, 0, 0, 0.6));
  font-size: 0.875rem;
}

/* Screen reader only */
.sr-only {
  position: absolute;
  width: 1px;
  height: 1px;
  padding: 0;
  margin: -1px;
  overflow: hidden;
  clip: rect(0, 0, 0, 0);
  white-space: nowrap;
  border-width: 0;
}

/* Responsive adjustments */
@media (max-width: 768px) {
  .logo-carousel-item {
    height: 3rem;
    min-width: 4rem;
    max-width: 8rem;
  }
}

@media (max-width: 480px) {
  .logo-carousel-item {
    height: 2.5rem;
    min-width: 3rem;
    max-width: 6rem;
  }
}

/* High contrast mode support */
@media (prefers-contrast: high) {
  .logo-carousel-control-button {
    border-width: 2px;
  }

  .logo-carousel-logo-wrapper:focus-visible {
    outline-width: 3px;
  }
}

/* Dark mode adaptation (if host page supports it) */
@media (prefers-color-scheme: dark) {
  .logo-carousel-control-button {
    background-color: var(--carousel-control-bg, rgba(255, 255, 255, 0.1));
